sys_variable_value.value:
//Defining period

var gdt = new GlideDateTime();
var days = gdt.getDaysInMonthUTC();
var start = gdt.toString();
var end = days.toString();
var year = start.substr(0, 8);
var dates = "" + year + "01," + "" + year + days + "";

//REST Call

var endpoint = "https://api.rawg.io/api/games?key=27add0a42a4e45ceaee73bc52fc7ffa5&ordering=-metacritic&dates=" + "" + dates + "";
var req = new sn_ws.RESTMessageV2();
req.setEndpoint(endpoint);
req.setHttpMethod('GET');
var response = req.execute();
var responseBody = response.getBody();
var httpStatus = response.getStatusCode();

//Defining game

var results = JSON.parse(responseBody).results[0].name;

