sys_variable_value.value:
(function execute(inputs, outputs) {
//Defining period

var gdt = new GlideDateTime();
var days = gdt.getDaysInMonthUTC();
var start = gdt.toString();
var end = days.toString();
var year = start.substr(0, 8);
var dates = '' + year + '01,' + '' + year + days + '';

//REST Call

var req = new sn_ws.RESTMessageV2('x_885223_install_h.game_call', 'GET');
req.setStringParameter('query','dates=' + dates + '');
var response = req.execute();
var responseBody = response.getBody();
var httpStatus = response.getStatusCode();

//Defining game

var title = JSON.parse(responseBody).results[0];
var results = 'The best game this month is ' + title.name + '.';
var metacritic = title.metacritic;
if (metacritic == null) {
	results = 'There are no good games this month yet. Spend some time outside instead!';
}
outputs.game = results;

})(inputs, outputs);

